import React, { useState } from "react";
import ReactDOM from "react-dom";
import Card from './newscard'
import axios from 'axios';


class News extends React.Component {

    constructor() {
        super();
        this.state = {
            articles: this.articles
        }
    }
    componentDidMount() {
        fetch("https://free-news.p.rapidapi.com/v1/search?q=%20agriculture%20and%20farmers%20welfare&lang=en", {
            "method": "GET",
            "headers": {
                "x-rapidapi-host": "free-news.p.rapidapi.com",
                "x-rapidapi-key": "c2c16bd498mshbe8d539aec12942p166ddcjsn5b0358c7847f"
            }
        })
        .then(response => {
            console.log(response);
            
        })
        .catch(err => {
            console.error(err);
        });
    }
    render() {
        return (
            < div >
                <h1>AGRICULTURE AND FARMERS WELFARE NEWS</h1>
                <div className="row">
                    {this.state.articles.map((article) => {
                        return <div className="col-md-4" key={article.url}>
                                ]<Card title={article.title} summary={article.summary} />
                        </div>
                    })}
                </div>
            </div >
        )
    }
}

export default News;
