import React, { useState } from "react";
import ReactDOM from "react-dom";
import axios from 'axios';

const News = () => {

    const [data, setData] = useState([]);
    const Api_key ="c00f12b363c4629f514c534b8eff91d3";
    const baseURL = `http://api.scraperapi.com/?api_key=${Api_key}&autoparse=true`;
    const newURL ='https://economictimes.indiatimes.com/news/ministry-of-agriculture-and-farmers-welfare';

    const fetchnews = () => {
        axios.get(`${baseURL}&url=${newURL}`)
        .then((response) => {
                console.log(response);
                setData(response);
            });
    }
    return (
        <div>
            <h1>Hii There!!!</h1>
            <button onClick={fetchnews}>Fetch News</button>
            {data.map((element)=>{
                return(
                 <div className="news">
                 <div className="card mb-3" style={{maxWidth:"540px"}} >
                     <div className="row g-0">
                         <div className="col-md-4">
                             <img src="..." className="img-fluid rounded-start" alt="..." />
                         </div>
                         <div className="col-md-8">
                             <div className="card-body">
                                 <h5 className="card-title">{element.title}</h5>
                                 <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                                 <p className="card-text"><small className="text-muted">Last updated 3 mins ago</small></p>
                             </div>
                         </div>
                     </div>
                 </div>
             </div>)

            })}
           

        </div>
    )

}
export default News;
